import React from "react";
import { useStateValue } from "./StateProvide";
import "./Checkout.css";
import CheckoutProduct from "./CheckoutProduct.js";

function Checkout() {
  const [{ basket }, dispatch] = useStateValue();
  return (
    <div className="checkout">
      <div className="checkout__left">
        <img
          className="checkout__ad"
          src="    
        banner2.jpg"
          alt=""
        />
        {basket?.length === 0 ? (
          <div>
            <h2>Your Shopping Basket is Empty</h2>
          </div>
        ) : (
          <div>
            <h2 className="checkout__title"> Your Shopping Product</h2>
            {basket.map((item) => (
              <CheckoutProduct
                id={item.id}
                title={item.title}
                image={item.image}
                price={item.price}
                rating={item.rating}
              />
            ))}
          </div>
        )}
      </div>
      {basket.length > 0 && <div className="checkout__right"></div>}
    </div>
  );
}

export default Checkout;
