export const initialState = {
  basket: [
    (id = "1321215"),
    (title = "AmazonBasics Book Safe, Key Lock, Small, Blue"),
    (price = 20.56),
    (rating = 3),
    (image =
      "https://m.media-amazon.com/images/I/91E1LbRmTYL._AC_UY327_FMwebp_QL65_.jpg"),
  ],
  user: null,
};

function Reducer(state, action) {
  console.log(action);
  switch (action.type) {
    case "ADD_TO_BASKET":
      return {
        ...state,
        basket: [...state.basket, action.item],
      };
      break;
    case "REMOVE_FROM_BASKET":
      return { state };
      break;
    default:
      return state;
  }
}

export default Reducer;
